import React, { forwardRef, useImperativeHandle, useState } from 'react';

const Button = forwardRef((ref, props) => {
  // toggle 切换
  const [toggle, setToggle] = useState(false);

  useImperativeHandle(ref, () => ({
    alterToggle() {
      setToggle(!toggle);
    },
  }));
  return (
    <>
      <button
        onClick={() => {
          setToggle(!toggle);
        }}
      >
        Button From Child
      </button>

      {toggle && <div>Toggle</div>}
    </>
  );
});

export default Button;
