import React, { useState } from 'react';

const Button = () => {
  // toggle 切换
  const [toggle, setToggle] = useState(false);
  return (
    <>
      <button
        onClick={() => {
          setToggle(!toggle);
        }}
      >
        Button From Child
      </button>

      {toggle && <div>Toggle</div>}
    </>
  );
};

export default Button;
